let MockStorage: typeof Storage | undefined

// @ts-ignore
if (process.env.MODULE_FORMAT !== 'umd') {
  MockStorage = class implements Storage {
    [index: number]: string;
    [key: string]: any;

    public get length(): number {
      return Object.keys(this).length
    }

    public key(index: number): string | any {
      return Object.keys(this)[index]
    }

    public setItem(key: string, data: any): void {
      this[key] = data.toString()
    }
    public getItem(key: string): string {
      return this[key]
    }
    public removeItem(key: string): void {
      delete this[key]
    }
    public clear(): void {
      for (let key of Object.keys(this)) {
        delete this[key]
      }
    }
  }
}

export { MockStorage }
// tslint:disable: variable-name
export default class SimplePromiseQueue {
  private readonly _queue: Array<Promise<void>> = []
  private _flushing = false

  public enqueue(promise: Promise<void>) {
    this._queue.push(promise)
    if (!this._flushing) { return this.flushQueue() }
    return Promise.resolve()
  }

  private flushQueue() {
    this._flushing = true

    const chain = (): Promise<void> | void => {
      const nextTask = this._queue.shift()
      if (nextTask) {
        return nextTask.then(chain)
      } else {
        this._flushing = false
      }
    }
    return Promise.resolve(chain())
  }
}
